mapper.select:
/* FAQ 목록 조회 */
SELECT RNUM, F_NO, F_TITLE, F_CONTENT, TO_CHAR(F_CREATED_AT, 'YYYY-MM-DD') AS F_CREATED_AT, TO_CHAR(F_UPDATED_AT, 'YYYY-MM-DD') AS F_UPDATED_AT, TO_CHAR(F_DELETED_AT, 'YYYY-MM-DD') AS F_DELETED_AT FROM ( SELECT ROWNUM AS RNUM, F_NO, F_TITLE, F_CONTENT, F_CREATED_AT, F_UPDATED_AT, F_DELETED_AT FROM TB_ADMIN_FAQ WHERE
ROWNUM <= #{pageNum} * #{amount} ) FAQLIST WHERE RNUM > (#{pageNum}-1) * #{amount} ORDER BY F_NO

/* FAQ 목록 조회 */
SELECT rnum, F_NO, F_TITLE, F_CONTENT, TO_CHAR(F_CREATED_AT, 'YYYY-MM-DD') AS F_CREATED_AT, TO_CHAR(F_UPDATED_AT, 'YYYY-MM-DD') AS F_UPDATED_AT, F_READCNT FROM ( SELECT ROWNUM as rnum, F_NO, F_TITLE, F_CONTENT, F_CREATED_AT, F_DELETED_AT, F_UPDATED_AT, F_READCNT FROM TB_ADMIN_FAQ WHERE
ROWNUM <= #{pageNum} * #{amount} ) FAQLIST WHERE F_DELETED_AT IS NULL AND rnum > (#{pageNum}-1) * #{amount} ORDER BY F_NO

mapper.update:
UPDATE TB_ADMIN_FAQ SET F_CONTENT = #{f_content}, F_UPDATED_AT = SYSDATE
, F_DELETED_AT = NULL
WHERE F_NO = #{f_no}

UPDATE TB_ADMIN_FAQ
SET F_READCNT = F_READCNT + 1
WHERE F_NO = #{f_no}
