mapper.select:
SELECT AN_NO, A_NO, AN_TITLE, AN_CONTENT, AN_CATEGORY, TO_CHAR(AN_CREATED_AT, 'YYYY-MM-DD') AS AN_CREATED_AT, AN_CNT, AN_THUMBNAIL, AN_FILE FROM ( SELECT /*+ INDEX_DESC(tb_admin_notice tb_admin_notice_pk) */ rownum as rnum, an_no, a_no, an_title, an_content, an_category, an_created_at, an_cnt, an_thumbnail, an_file FROM tb_admin_notice WHERE
rownum <= #{pageNum} * #{amount} ) noticelist WHERE rnum > (#{pageNum}- 1) * #{amount}
AND an_category = '[공지사항]'

SELECT AN_NO, A_NO, AN_TITLE, AN_CONTENT, AN_CATEGORY, TO_CHAR(AN_CREATED_AT, 'YYYY-MM-DD') AS AN_CREATED_AT, AN_CNT, AN_THUMBNAIL, AN_FILE FROM ( SELECT /*+ INDEX_DESC(tb_admin_notice tb_admin_notice_pk) */ rownum as rnum, an_no, a_no, an_title, an_content, an_category, an_created_at, an_cnt, an_thumbnail, an_file FROM tb_admin_notice WHERE
rownum <= #{pageNum} * #{amount} ) noticelist WHERE rnum > (#{pageNum}- 1) * #{amount}
AND an_category = '[이벤트]'

mapper.update:
/* Notice - noticeCntUpdate */
UPDATE tb_admin_notice
SET an_cnt = an_cnt + 1
WHERE an_no = #{an_no}

mapper.select:
/* Notice - noticeDetail */
SELECT
	an_no, a_no, an_title, an_content, TO_CHAR(an_created_at, 'YYYY-MM-DD HH24:MI:SS') AS an_created_at, an_cnt, an_thumbnail, an_file
FROM tb_admin_notice
WHERE an_no = #{an_no}

mapper.update:
/* Notice - noticeUpdate */
UPDATE tb_admin_notice
SET an_title = #{an_title},
	an_content = #{an_content},
	an_created_at = sysdate

		, an_thumbnail = #{an_thumbnail}


		, an_file = #{an_file}

WHERE an_no = #{an_no}
